casper.test.begin('Widget - Avocet Header', function(test) {

    /**
     * Verify that the Avocet Header is shown on the page
     */
    var verifyHeader = function() {
        // Verify that the header exists on the page
        test.assertExists('#oa-avocetheader', 'Verify that the header exists on the page');
        // Verify that the header shows the Cambridge logo
        test.assertExists('#oa-avocetheader a[title="University of Cambridge home"] img', 'Verify that the header has the Cambridge logo');
        // Verify that the header has a 'Home' link
        test.assertExists('#oa-avocetheader #oa-avocetheader-nav-container .oa-nav.depth-1 a[href="/"]', 'Verify that the header has a \'Home\' link');
        // Verify that the header has a 'Learn more' link
        test.assertExists('#oa-avocetheader #oa-avocetheader-nav-container .oa-nav.depth-1 a[href="/what-do-i-need-to-do"]', 'Verify that the header has a \'Learn more\' link');
        // Verify that the header 'Learn more' link has a submenu
        test.assertExists('#oa-avocetheader #oa-avocetheader-nav-container .oa-nav.depth-1 a[href="/what-do-i-need-to-do"] + .oa-nav.depth-2', 'Verify that the \'Learn more\' link has a submenu');
        // Verify that the header 'Learn more' link submenu has a 'What do I need to do?' Link
        test.assertExists('#oa-avocetheader #oa-avocetheader-nav-container .oa-nav.depth-2 a[href="/what-do-i-need-to-do"]', 'Verify that the \'Learn more\' link submenu has a \'What do I need to do?\' link');
        // Verify that the header 'Learn more' link submenu has a 'What's changing?' Link
        test.assertExists('#oa-avocetheader #oa-avocetheader-nav-container .oa-nav.depth-2 a[href="/what-is-changing"]', 'Verify that the \'Learn more\' link submenu has a \'What\'s changing?\' link');
        // Verify that the header 'Learn more' link submenu has a 'What is Open Access?' Link
        test.assertExists('#oa-avocetheader #oa-avocetheader-nav-container .oa-nav.depth-2 a[href="/what-is-open-access"]', 'Verify that the \'Learn more\' link submenu has a \'What is Open Access?\' link');
        // Verify that the header has a 'Sign in' button
        test.assertExists('#oa-avocetheader #oa-avocetheader-authentication-container #oa-avocetheader-login', 'Verify that the header has a \'Sign in\' button');
        // Verify that the header has a 'Upload manuscript' button
        test.assertExists('#oa-avocetheader #oa-avocetheader-authentication-container #oa-avocetheader-upload', 'Verify that the header has a \'Upload manuscript\' button');
    };

    casper.start(configUtil().tenantUI, function() {
        // Verify that the header is showing
        casper.then(function() {
            casper.echo('# Verify that the header shows on the page', 'INFO');
            casper.waitForSelector('#oa-avocetfooter', verifyHeader);
        });
    });

    casper.run(function() {
        test.done();
    });
});
