casper.echo('Activity - Test');

/**
 * Verify that the activity create discussion is shown
 *
 * @param    {Object}    discussion1        The discussion Object of the first discussion that was made
 * @param    {Object}    user             The user object of the user that made the discussions
 * @param    {Int}        n                 The index of the discussions activity in the activity list
 *                                        (higher if it was made first, lower if it was made last)
 */
var verifyCreatedDiscussions = function(discussion1, user, n) {
    // Assert the sentence '"username" started the discussion "discussionname"'
    // Assert also the references to the user and discussion
    casper.test.assertExists('ul.oae-list>li:nth-child(' + n + ') div.activity-summary>a[href="' + user.profilePath + '"]', 'The ref to the user in the activity "\'username\' started the discussion \'discussionname\'" is present');
    casper.test.assertSelectorHasText('ul.oae-list>li:nth-child(' + n + ') div.activity-summary>a', user.displayName,  'The name of the user in the activity "\'username\' started the discussion \'discussionname\'" is present');
    casper.test.assertSelectorHasText('ul.oae-list>li:nth-child(' + n + ') div.activity-summary','started the discussion', '"started the discussion" sentence in the activity "\'username\' started the discussion \'discussionname\'" is present');
    casper.test.assertExists('ul.oae-list>li:nth-child(' + n + ') div.activity-summary>a[href="' + discussion1.profilePath + '"]', 'The ref to the discussion in the activity "\'username\' started the discussion \'discussionname\'" is present');
    casper.test.assertSelectorHasText('ul.oae-list>li:nth-child(' + n + ') div.activity-summary>a', discussion1.displayName, 'The name of the discussion in the activity "\'username\' started the discussion \'discussionname\'" is present');
    // Assert that the sentence "and 3 others" is added if four of the same activity are made
    casper.test.assertSelectorHasText('ul.oae-list>li:nth-child(' + n + ') div.activity-summary','and 3 others', 'The sentence "and 3 others" is present if 3 of the same activity are made');
    // The normal preview has a large thumbnail of the activity
    casper.test.assertExists('ul.oae-list>li:nth-child(' + n + ') div.oae-thumbnail-large', 'The first created activity has a large icon');
    // The third of the same activity doesn't have a large thumbnail
    casper.test.assertDoesntExist('ul.oae-list>li:nth-child(' + n + ') div.activity-multiple div.oae-thumbnail-large', 'The third created activity doesn\'t have a large icon');
    // The third of the same activity does have a small thumbnail
    casper.test.assertExists('ul.oae-list>li:nth-child(' + n + ') div.activity-multiple div.oae-thumbnail.icon-oae-discussion', 'The third created activity has a small icon');
};

