casper.test.comment('Widget - Link preview');

/**
 * Verifies that the link is shown in an iframe on the content profile
 */
var verifyLinkPreview = function() {
    casper.waitForSelector('#linkpreview-container', function() {
        casper.test.assertExists('#linkpreview-container', 'The link preview container is present');
        casper.test.assertExists('#linkpreview-container iframe', 'The link preview iframe is present');
    });
};

casper.start('http://test.oae.com', function() {
    // Create a couple of users to test with
    var user1 = null;
    var user2 = null;
    userUtil().createUsers(2, function(users) {
        user1 = users[0];
        user2 = users[1];
    });

    // Log in with that user
    casper.then(function() {
        userUtil().doLogIn(user1.username, 'password');
    });

    // Verify default previews
    casper.then(function() {
        casper.echo('Verify link preview elements present', 'INFO');
        contentUtil().createLink('http://oae.sakaiproject.org/', function(contentURL) {
            casper.thenOpen('http://test.oae.com' + contentURL);

            casper.then(function() {
                verifyLinkPreview();
            });
        });
    });

    // Log out at the end of the test
    casper.then(function() {
        userUtil().doLogOut();
    });
});

casper.run(function() {
    this.test.done();
});
